import {GET_POSTS,POST_ERROR} from '../actions/types';
const initialState={
    posts:[],
    post:null,
    loading:true,
    errors:[]
}
const post=(state=initialState,action)=>{
    const {type,payload}=action;
    switch(type){
        case GET_POSTS:
            return {...state,posts:payload,loading:false};
        case POST_ERROR:
            return {...state,errors:payload,loading:false};
        default :
            return state;
    }

}
export default post;
