import React from 'react';
import Moment from 'react-moment';
import { useSelector } from 'react-redux';
const PostItem = ({post:{text,name,avatar,id,date,likes,comments,user}}) => {
    const {isLoading}=useSelector(state=>state.auth);

  return (<div class="post bg-white p-1 my-1">
          <div>
            <a href="profile.html">
              <img
                class="round-img"
                src={avatar}
                alt=""
              />
              <h4>{name}</h4>
            </a>
          </div>
          <div>
            <p class="my-1">
              {text}
            </p>
             <p class="post-date">
                Posted on <Moment format="YYYY/MM/DD">{date}</Moment>
            </p>
            <button type="button" class="btn btn-light">
              <i class="fas fa-thumbs-up"></i>{' '}<span>{likes.length>0 && <span>{likes.length}</span> }</span>
              
            </button>
            <button type="button" class="btn btn-light">
              <i class="fas fa-thumbs-down"></i>
            </button>
            <a href="post.html" class="btn btn-primary">
              Discussion <span class='comment-count'>2</span>
            </a>
            {!isLoading && user===auth.user._id &&  <button      
                                                        type="button" class="btn btn-danger"><i class="fas fa-times"></i>
                                                    </button>
            }
           
          </div>
        </div>);
};

export default PostItem;
