import React from 'react';
import Moment from 'react-moment';
const CommentItem = ({comment:{_id,text,user,name,avatar,date}}) => {
  return (
    <div class="post bg-white p-1 my-1">
        <div>
            <a href="profile.html">
                <img
                class="round-img"
                src={avatar}
                alt=""
                />
                <h4>{name}</h4>
            </a>    
        </div>
        <div>
        <p class="my-1">
            {text}
        </p>
        <p class="post-date">
            Posted on <Moment format='YYYY/MM/DD'>{date}</Moment>
        </p>
        </div>
    </div>
  );
};

export default CommentItem;
